CREATE DEFINER=`root`@`localhost` FUNCTION `GCR_STATUSES` (`status` VARCHAR(50)) RETURNS VARCHAR(100) CHARSET utf8mb4 COLLATE utf8mb4_general_ci  BEGIN
    DECLARE status_text VARCHAR(100);
    
    CASE status
        WHEN 'pending_submission' THEN SET status_text = 'Draft';
        WHEN 'pending_hr1' THEN SET status_text = 'Pending HR Verification';
        WHEN 'pending_gm' THEN SET status_text = 'Pending GM Approval';
        WHEN 'pending_hr2' THEN SET status_text = 'Pending HR Final Recording';
        WHEN 'approved' THEN SET status_text = 'Approved';
        WHEN 'rejected' THEN SET status_text = 'Rejected';
        ELSE SET status_text = 'Unknown';
    END CASE;
    
    RETURN status_text;
END$$

